mapper.select:


    del_flag = 0

        AND ip_address LIKE CONCAT('%', #{ipAddress}, '%')


        AND block_type = #{blockType}


        AND status = #{status}


ORDER BY create_time DESC

mapper.update:
UPDATE ws_ip_blacklist
block_reason = #{blockReason}, block_type = #{blockType}, expire_time = #{expireTime}, status = #{status}, remark = #{remark}, update_by = #{updateBy}, update_time = NOW()
WHERE id = #{id}

UPDATE ws_ip_blacklist 
SET hit_count = hit_count + 1, last_hit_time = NOW(), update_time = NOW() 
WHERE id = #{id}
