mapper.select:


     and user_id = #{userId}
     and method_name like concat('%', #{methodName}, '%')
     and description like concat('%', #{description}, '%')
     and execution_result like concat('%', #{executionResult}, '%')
     and method_params like concat('%', #{methodParams}, '%')
     and execution_time between #{params.beginExecutionTime} and #{params.endExecutionTime}
     and execution_time_millis >= #{params.executionTimeMillisMin}
     and execution_time_millis <= #{params.executionTimeMillisMax}
     and is_success = #{isSuccess}



            and (description like '%警告%' or description like '%WARN%' or description like '%warn%'
                 or execution_result like '%警告%' or execution_result like '%WARN%' or execution_result like '%warn%')



order by execution_time desc

mapper.update:
update wc_log_info
user_id = #{userId}, method_name = #{methodName}, description = #{description}, method_params = #{methodParams}, execution_time = #{executionTime}, execution_result = #{executionResult}, execution_time_millis = #{executionTimeMillis}, is_success = #{isSuccess},
where id = #{id}

mapper.delete:
delete from wc_log_info

     and user_id = #{userId}
     and method_name like concat('%', #{methodName}, '%')
     and description like concat('%', #{description}, '%')
     and execution_result like concat('%', #{executionResult}, '%')
     and method_params like concat('%', #{methodParams}, '%')
     and execution_time between #{params.beginExecutionTime} and #{params.endExecutionTime}
     and execution_time_millis >= #{params.executionTimeMillisMin}
     and execution_time_millis <= #{params.executionTimeMillisMax}
     and is_success = #{isSuccess}


            and (description like '%警告%' or description like '%WARN%' or description like '%warn%'
                 or execution_result like '%警告%' or execution_result like '%WARN%' or execution_result like '%warn%')
